-- Column names
SELECT lower(name) AS name_columns FROM pragma_table_info("thisisatest") ORDER BY lower(name);
---------- -- Column types -- fixed types WITH flex_cols AS (SELECT lower(unnest(map_keys(MAP {}))) AS name_type), student_datatype AS (SELECT lower(name) AS name_type, TYPE AS datatype FROM pragma_table_info("thisisatest") ORDER BY name) SELECT lower(name_type), datatype FROM student_datatype ANTI JOIN flex_cols ON student_datatype.name_type = flex_cols.name_type ORDER BY lower(name_type);
-- flexible types WITH flextype_table AS (SELECT unnest(map_entries(MAP {}), RECURSIVE := TRUE)), student_datatype AS (SELECT lower(name) AS name_type, TYPE AS datatype FROM pragma_table_info("thisisatest") ORDER BY name), flextype_flat AS (SELECT lower("key") AS col, unnest("value") AS datatype FROM flextype_table), correct_types AS (SELECT flextype_flat.col AS flexible_column_name FROM flextype_flat JOIN student_datatype ON flextype_flat.col = student_datatype.name_type AND flextype_flat.datatype = student_datatype.datatype) SELECT lower(flexible_column_name), '## viable_flex_type ##' AS flex_type FROM correct_types UNION ALL SELECT DISTINCT lower(flextype_flat.col) AS flexible_column_name, '## non_viable_flex_type ##' AS flex_type FROM flextype_flat ANTI JOIN correct_types ON flextype_flat.col = correct_types.flexible_column_name;
---------- -- Not null columns
SELECT lower(name) AS name_notnull_nopk FROM pragma_table_info("thisisatest") WHERE "notnull" AND NOT "pk" AND NOT "dflt_value" ORDER BY lower(name);
---------- -- Unique constraint
SELECT lower(unnest(constraint_column_names)) AS name_unique FROM duckdb_constraints() WHERE SCHEMA_NAME='main' AND constraint_type = 'UNIQUE' AND lower(TABLE_NAME)=lower('thisisatest') ORDER BY lower(name_unique);
---------- -- Primary key columns
SELECT lower(name) AS name_primarykey FROM pragma_table_info("thisisatest") WHERE pk ORDER BY lower(name);
---------- -- Foreign key columns
SELECT lower(unnest(constraint_column_names)) AS name_fk_from, lower(constraint_text) AS fk_constraint_text FROM duckdb_constraints() WHERE SCHEMA_NAME='main' AND constraint_type = 'FOREIGN KEY' AND lower(TABLE_NAME)=lower('thisisatest') ORDER BY lower(name_fk_from);

